import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
    
    constructor() {
        super();
        this.state = {
                articles: [],
                laoding: false,
                page:1
        }
    }


       async componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=63dc337cc4e340669e1b87b02cef33f2&page=1";
        let data = await fetch(url);
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({articles: parsedData.articles})
     }

     handlPrevClick = async()=>{
       console.log("Previous");
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=63dc337cc4e340669e1b87b02cef33f2&page=${this.state.page - 1}`;
        let data = await  fetch(url);
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({
        page: this.state.page - 1,
        articles: parsedData.articles})


     }

     handleNextClick = async()=>{
        console.log("Next");
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=63dc337cc4e340669e1b87b02cef33f2&page=${this.state.page + 1}`;
        let data = await  fetch(url);
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({
        page: this.state.page + 1,
        articles: parsedData.articles
      })
    }




    render() {
      console.log("render")
        return (
            <div className="container my-3">
                <h1>NewsMonkey - TopHeadlines</h1>
                <div className="row">
                {this.state.articles.map((element)=>{
                  return   <div className="col-md-4" key={element.url}>
                    <NewsItem  title={element.title?element.title:""} description={element.description?element.description:""} imageurl={element.urlToImage} newsurl={element.url}/>
                    </div>
                })}                                           
                </div>
                <div className="conatiner d-flex justify-content-between">
                <button disabled={this.state.page<=1} type="button" class="btn btn-dark" OnClick={this.handlPrevClick}> &larr; Previous</button>
                <button type="button" class="btn btn-dark" OnClick={this.handleNextClick}>Next &rarr;</button>
                </div>
            </div>
        )
    }
}

export default News
